import java.util.Arrays;public class ArrayTest{	public static void main(String[] args){		int[][] array = new int[2][3];		System.out.println(array instanceof int[][]);		int m = 0;		for(int i = 0; i < array.length; i++){			for(int j = 0; j < array[i].length; j++){				m++;				array[i][j] = m * 2;			}		}		for(int i = 0; i < array.length; i++){			for(int j = 0; j < array[i].length; j++){				System.out.print(array[i][j] + " ");			}			System.out.println();		}		int [][] arr = {{1, 2, 3},{4},{5, 6, 7, 8}};		for(int i = 0; i < arr.length; i++){			for(int j = 0; j < arr[i].length; j++){				System.out.print(arr[i][j] + " ");			}			System.out.println();		}				System.out.println("-----------------------");		int[] a = {1, 2, 3};		int[] b = {1, 2, 3};		System.out.println(isEquals(a, b));		System.out.println("-----------------------");		System.out.println(Arrays.equals(a, b));			}	public static boolean isEquals(int[] a, int[] b){		if(a == null || b == null){			return false;		}		if(a.length != b.length){			return false;		}		for(int i = 0; i < a.length; i++){			if(a[i] != b[i]){				return false;			}		}		return true;	}}