import java.util.*;public class CollectionCount{	public static void main(String[] args){		Random rand = new Random();		HashMap map = new HashMap();				for(int i = 0; i < 50; i++){			int in = rand.nextInt(41) + 10;			System.out.print(in + " ");			if(map.get(new Integer(in)) == null){				map.put(new Integer(in), new Integer(1));			}else{				int value = ((Integer)map.get(in)).intValue();				map.put(new Integer(in), new Integer(value + 1));			}					}		System.out.println();		Collection collection = map.values();		Integer max = (Integer)Collections.max(collection);		ArrayList list = new ArrayList();		Set set = map.entrySet();		for(Iterator iter = set.iterator(); iter.hasNext();){			Map.Entry entry = (Map.Entry)iter.next();			Integer key = (Integer)entry.getKey();			Integer value = (Integer)entry.getValue();			if(max.equals(value)){				list.add(key);			}			System.out.println(key.intValue() + " : " + value.intValue());		}		System.out.println("出现最多的次数是：" + max);		System.out.println("出现的数是：");		for(Iterator iter = list.iterator(); iter.hasNext();){			System.out.println(iter.next());		}	}}