import java.util.Arrays;import java.util.Random;public class CountArrayTest{	public static void main(String[] args){		Random rand = new Random();		int[] arr = new int[50];		for(int i = 0; i < arr.length; i++){			arr[i] = rand.nextInt(50);		}		Arrays.sort(arr);		for(int i = 0; i < arr.length; i++){			System.out.print(arr[i] + " ");		}		System.out.println();		for(int i = 0; i < 50; i++){			int index = Arrays.binarySearch(arr, i);			/*			System.out.println(i + ": " +index + " ");			System.out.println();			*/			if(index >= 0){				int times = 0;				for(int j = 0; j < arr.length; j++){					if(arr[index] == arr[j]){						times++;					}				}				System.out.println(arr[index] + ": " + times);			}		}	}}