public class DualLinkTest{	public static void main(String[] args){		LinkNode node1 = new LinkNode("node1");		LinkNode node2 = new LinkNode("node2");		LinkNode node3 = new LinkNode("node3");				node1.next = node2;		node2.next = node3;		node3.next = node1;		node1.previous = node3;		node2.previous = node1;		node3.previous = node2;		System.out.println(node1.next.next.next.data);		System.out.println(node3.previous.previous.data);				LinkNode node4 = new LinkNode("node4");		node4.next = node2;		node1.next = node4;				node4.previous = node1;		node2.previous = node4;		System.out.println(node3.next.next.data);		System.out.println(node3.previous.previous.data);					}}class LinkNode{	LinkNode previous;	String data;	LinkNode next;	public LinkNode(String data){		this.data = data;	}}