public class EqualsTest{	public static void main(String[] args){		Student s1 = new Student("jack");		Student s2 = new Student("jack");		System.out.println(s1 == s2);		System.out.println(s1.equals(s2));	}}class Student{	private String name;	public Student(String name){		this.name = name;	}	public boolean equals(Object anObject){		if(this == anObject){			return true;		}		if(anObject instanceof Student)	{			Student student = (Student)anObject;			if(this.name.equals(student.name)){				return true;			}		}				return false;	}}