import java.util.*;public class Gerbil{	private int gerbilNumber;	public Gerbil(int gerbilNumber){		this.gerbilNumber = gerbilNumber;	}	public void hop(){		System.out.println("gerbil " + gerbilNumber + " is jumping.");			}		public static void main(String[] args){		ArrayList<Gerbil> gerbils = new ArrayList<Gerbil>();		for(int i = 0; i < 5; i ++){			gerbils.add(new Gerbil(i));		}				for(int i = 0; i < gerbils.size(); i ++){			gerbils.get(i).hop();		}				for(Gerbil g : gerbils){			g.hop();		}	}}