public class SingletonTest{	public static void main(String[] args){		Singleton singleton = Singleton.getInstance();		Singleton anotherSingleton = Singleton.getInstance();		System.out.println(singleton == anotherSingleton);	}}class Singleton{	private static Singleton singleton;	private Singleton(){}	public static Singleton getInstance(){		if(singleton == null){			singleton = new Singleton();		}				return singleton;	}}